// api.js
export const fetchAdvertisements = async () => {
    try {
      const response = await fetch('https://your-api-endpoint.com/advertisements');
      if (!response.ok) {
        throw new Error('Failed to fetch advertisements');
      }
      return await response.json();
    } catch (error) {
      console.error('Error fetching advertisements:', error);
      // Return demo data in case of failure
      return [
        { id: 1, content: 'Demo Advertisement 1', link: '#',imageUrl:'data:image/jpeg;base64,'},
        { id: 2, content: 'Demo Advertisement 2', link: '#',imageUrl:'data:image/jpeg;base64,' },
        { id: 3, content: 'Demo Advertisement 3', link: '#' }
      ];
    }
  };
  